pub fn &lt;span style=&#34;color:#a6e22e&#34;&gt;build&lt;/span&gt;(b: &lt;span style=&#34;color:#f92672&#34;&gt;*&lt;/span&gt;Builder) &lt;span style=&#34;color:#66d9ef&#34;&gt;void&lt;/span&gt; {
&lt;span style=&#34;color:#75715e&#34;&gt;// Standard target options allows the person running `zig build` to choose
&lt;/span&gt;&lt;span style=&#34;color:#75715e&#34;&gt;&lt;/span&gt; &lt;span style=&#34;color:#75715e&#34;&gt;// what target to build for. Here we do not override the defaults, which
&lt;/span&gt;&lt;span style=&#34;color:#75715e&#34;&gt;&lt;/span&gt; &lt;span style=&#34;color:#75715e&#34;&gt;// means any target is allowed, and the default is native. Other options
&lt;/span&gt;&lt;span style=&#34;color:#75715e&#34;&gt;&lt;/span&gt; &lt;span style=&#34;color:#75715e&#34;&gt;// for restricting supported target set are available.
&lt;/span&gt;&lt;span style=&#34;color:#75715e&#34;&gt;&lt;/span&gt; &lt;span style=&#34;color:#66d9ef&#34;&gt;const&lt;/span&gt; target &lt;span style=&#34;color:#f92672&#34;&gt;=&lt;/span&gt; b.standardTargetOptions(.{});
&lt;span style=&#34;color:#75715e&#34;&gt;// Standard release options allow the person running `zig build` to select
&lt;/span&gt;&lt;span style=&#34;color:#75715e&#34;&gt;&lt;/span&gt; &lt;span style=&#34;color:#75715e&#34;&gt;// between Debug, ReleaseSafe, ReleaseFast, and ReleaseSmall.
&lt;/span&gt;&lt;span style=&#34;color:#75715e&#34;&gt;&lt;/span&gt; &lt;span style=&#34;color:#66d9ef&#34;&gt;const&lt;/span&gt; mode &lt;span style=&#34;color:#f92672&#34;&gt;=&lt;/span&gt; b.standardReleaseOptions();
&lt;span style=&#34;color:#66d9ef&#34;&gt;const&lt;/span&gt; exe &lt;span style=&#34;color:#f92672&#34;&gt;=&lt;/span&gt; b.addExecutable(&lt;span style=&#34;color:#e6db74&#34;&gt;&amp;#34;foo&amp;#34;&lt;/span&gt;, &lt;span style=&#34;color:#e6db74&#34;&gt;&amp;#34;src/main.zig&amp;#34;&lt;/span&gt;);
exe.setTarget(target);
exe.setBuildMode(mode);
exe.install();
&lt;span style=&#34;color:#66d9ef&#34;&gt;const&lt;/span&gt; run_cmd &lt;span style=&#34;color:#f92672&#34;&gt;=&lt;/span&gt; exe.run();
run_cmd.step.dependOn(b.getInstallStep());
&lt;span style=&#34;color:#66d9ef&#34;&gt;const&lt;/span&gt; run_step &lt;span style=&#34;color:#f92672&#34;&gt;=&lt;/span&gt; b.step(&lt;span style=&#34;color:#e6db74&#34;&gt;&amp;#34;run&amp;#34;&lt;/span&gt;, &lt;span style=&#34;color:#e6db74&#34;&gt;&amp;#34;Run the app&amp;#34;&lt;/span&gt;);
run_step.dependOn(&lt;span style=&#34;color:#f92672&#34;&gt;&amp;amp;&lt;/span&gt;run_cmd.step);
